                    gX.append('text')
                        .attr('class', 'label')
                        .attr('transform', 'translate(' + width / 2 + ',' + (margin.bottom - 6) + ')')
                        .attr('text-anchor', 'middle')
                        .html(xAxisLabel || xColumn);

                    var gY = svg.append('g')
                        .attr('class', 'y axis')
                        .call(yAxis);

                    gY.append('text')
                        .attr('class', 'label')
